import React, { useState, useEffect } from 'react';

// Base URL for your Spring Boot API
const API_URL = 'http://localhost:8080';

// --- Helper Functions for API Calls ---

const apiRequest = async (method, endpoint, body) => {
    try {
        const response = await fetch(`${API_URL}${endpoint}`, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
            },
            body: body ? JSON.stringify(body) : null,
        });

        if (response.status === 204) return null; // Handle No Content
        
        // Handle success status codes (200, 201)
        if (response.ok) {
            return await response.json();
        } else {
            // Throw an error with the HTTP status for specific feedback
            const errorText = response.statusText || 'Unknown Error';
            throw new Error(`API Error: ${response.status} - ${errorText}`);
        }
    } catch (error) {
        console.error('Fetch failed:', error);
        throw error;
    }
};

// --- Library Dashboard Component ---

function LibraryDashboard() {
    const [memberData, setMemberData] = useState({
        memberFirstName: '',
        memberLastName: '',
        email: '',
        memberStatus: 'Active',
        password: 'default_password', // Passwords are required by your entity
        joinDate: new Date().toISOString().slice(0, 10), // Default to today
        memberExpiryDate: '2026-12-31',
        dateOfBirth: '1990-01-01',
        address: '123 Test St',
        phoneNumber: '555-0101',
        gender: 'Male',
    });
    const [borrowData, setBorrowData] = useState({
        memberId: 1,
        bookId: 1,
        staffId: 1,
        dueDate: '2026-06-01',
    });
    const [returnId, setReturnId] = useState('');
    const [message, setMessage] = useState('');
    const [borrowList, setBorrowList] = useState([]);

    // Fetch existing borrow details on load
    useEffect(() => {
        const fetchBorrows = async () => {
            try {
                const data = await apiRequest('GET', '/borrow');
                setBorrowList(data || []);
            } catch (error) {
                setMessage(`Error fetching borrows: ${error.message}`);
            }
        };
        fetchBorrows();
    }, []);

    // Handlers for Forms
    const handleMemberChange = (e) => {
        const { name, value } = e.target;
        setMemberData({ ...memberData, [name]: value });
    };

    const handleBorrowChange = (e) => {
        const { name, value } = e.target;
        setBorrowData({ ...borrowData, [name]: value });
    };

    // --- API Action Functions ---

    const handleCreateMember = async () => {
        try {
            // NOTE: We don't need memberId for creation (it's auto-generated)
            const result = await apiRequest('POST', '/members', memberData);
            setMessage(`‚úÖ Member Created! ID: ${result.memberId}`);
        } catch (error) {
            setMessage(`‚ùå Failed to Create Member: ${error.message}`);
        }
    };

    const handleBorrowBook = async () => {
        try {
            const result = await apiRequest('POST', '/borrow', borrowData);
            setMessage(`‚úÖ Book Borrowed! Borrow ID: ${result.borrowId}`);
            // Update list after successful operation
            const updatedList = await apiRequest('GET', '/borrow');
            setBorrowList(updatedList || []);
        } catch (error) {
            setMessage(`‚ùå Borrow Failed: ${error.message}`);
        }
    };

    const handleReturnBook = async () => {
        if (!returnId) {
            setMessage('Please enter a Borrow ID to return.');
            return;
        }
        const returnDate = new Date().toISOString().slice(0, 10);
        try {
            const url = `/borrow/return/${returnId}?returnDate=${returnDate}`;
            const result = await apiRequest('PUT', url);
            setMessage(`‚úÖ Borrow ID ${result.borrowId} Returned on ${returnDate}`);
            
            const updatedList = await apiRequest('GET', '/borrow');
            setBorrowList(updatedList || []);
        } catch (error) {
            setMessage(`‚ùå Return Failed (ID ${returnId}): ${error.message}`);
        }
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial' }}>
            <h1>üìö Library Backend Demo</h1>
            
            {/* Status Message */}
            <div style={{ 
                padding: '10px', 
                backgroundColor: message.startsWith('‚úÖ') ? '#d4edda' : message.startsWith('‚ùå') ? '#f8d7da' : '#eee',
                border: message ? '1px solid #ccc' : 'none',
                marginBottom: '20px'
            }}>
                {message || 'Ready.'}
            </div>

            <div style={{ display: 'flex', gap: '40px' }}>
                
                {/* Member Management Form */}
                <div style={{ flex: 1, border: '1px solid #ccc', padding: '15px' }}>
                    <h2>üë§ Create New Member</h2>
                    {/* Only showing key input fields */}
                    <input type="text" name="memberFirstName" value={memberData.memberFirstName} onChange={handleMemberChange} placeholder="First Name" style={{ marginBottom: '10px', width: '90%' }} />
                    <input type="text" name="email" value={memberData.email} onChange={handleMemberChange} placeholder="Email" style={{ marginBottom: '10px', width: '90%' }} />
                    <select name="memberStatus" value={memberData.memberStatus} onChange={handleMemberChange} style={{ marginBottom: '10px', width: '90%', padding: '8px' }}>
                        <option value="Active">Active</option>
                        <option value="Not_Active">Not Active</option>
                    </select>
                    <button onClick={handleCreateMember} style={{ padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', cursor: 'pointer' }}>
                        POST /members
                    </button>
                </div>
                
                {/* Borrow/Return Cycle Form */}
                <div style={{ flex: 1, border: '1px solid #ccc', padding: '15px' }}>
                    <h2>üîÑ Borrow/Return Book</h2>
                    
                    <h3>Borrow (POST /borrow)</h3>
                    <input type="number" name="bookId" value={borrowData.bookId} onChange={handleBorrowChange} placeholder="Book ID" style={{ marginBottom: '10px', width: '90%' }} />
                    <input type="number" name="memberId" value={borrowData.memberId} onChange={handleBorrowChange} placeholder="Member ID" style={{ marginBottom: '10px', width: '90%' }} />
                    <input type="date" name="dueDate" value={borrowData.dueDate} onChange={handleBorrowChange} style={{ marginBottom: '10px', width: '90%', padding: '8px' }} />
                    <button onClick={handleBorrowBook} style={{ padding: '10px', backgroundColor: '#28a745', color: 'white', border: 'none', cursor: 'pointer' }}>
                        BORROW BOOK
                    </button>

                    <h3 style={{ marginTop: '20px' }}>Return (PUT /borrow/return/{returnId})</h3>
                    <input type="number" value={returnId} onChange={(e) => setReturnId(e.target.value)} placeholder="Enter Borrow ID to Return" style={{ marginBottom: '10px', width: '90%' }} />
                    <button onClick={handleReturnBook} style={{ padding: '10px', backgroundColor: '#dc3545', color: 'white', border: 'none', cursor: 'pointer' }}>
                        RETURN BOOK
                    </button>
                </div>

            </div>

            {/* List of Current Borrows */}
            <h2 style={{ marginTop: '30px' }}>Current Borrow Records</h2>
            <pre style={{ backgroundColor: '#f4f4f4', padding: '15px', borderRadius: '5px' }}>
                {JSON.stringify(borrowList, null, 2)}
            </pre>
        </div>
    );
}

export default LibraryDashboard;
