import React, { useState, useEffect } from 'react';

// Base URL for your Spring Boot API
const API_URL = 'http://localhost:8080';

// --- Helper Functions for API Calls ---

const apiRequest = async (method, endpoint, body) => {
    try {
        const response = await fetch(`${API_URL}${endpoint}`, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
            },
            body: body ? JSON.stringify(body) : null,
        });

        if (response.status === 204) return null;
        
        if (response.ok) {
            return response.headers.get('content-length') === '0' ? true : await response.json();
        } else {
            const errorText = await response.text();
            let parsedError = errorText || response.statusText || 'Unknown Error';
            
            try {
                const errorJson = JSON.parse(errorText);
                parsedError = errorJson.message || parsedError;
            } catch {
                // Ignore if not JSON
            }
            
            throw new Error(`API Error: ${response.status} - ${parsedError}`);
        }
    } catch (error) {
        console.error('Fetch failed:', error);
        throw error;
    }
};

// --- Library Dashboard Component ---

function LibraryDashboard() {
    // STATE FOR INPUT FORMS
    const [memberData, setMemberData] = useState({
        memberFirstName: 'New',
        memberLastName: 'User',
        email: `user_${Date.now()}@demo.com`, // Unique email for new inserts
        memberStatus: 'Active',
        password: 'test_pw', 
        joinDate: new Date().toISOString().slice(0, 10),
        memberExpiryDate: '2026-12-31',
        dateOfBirth: '1990-01-01',
        address: '123 Test St',
        phoneNumber: '555-0101',
        gender: 'Male',
    });
    const [borrowData, setBorrowData] = useState({
        memberId: 1,
        bookId: 1,
        staffId: 1,
        dueDate: '2026-06-01',
    });
    const [fineData, setFineData] = useState({
        borrowId: 2,
        staffId: 1,
        amount: 100,
        fineId: 1, // For payment
    });
    const [returnId, setReturnId] = useState('');
    const [message, setMessage] = useState('');
    
    // STATE FOR DISPLAY LISTS
    const [borrowList, setBorrowList] = useState([]);
    const [fineList, setFineList] = useState([]);

    // STATE FOR DEMO ROLE SIMULATION (Crucial for showing Admin/User behavior)
    const [currentProfile, setCurrentProfile] = useState({
        role: 'Staff', // Default to Staff for easy testing
        staffId: 1,     
        memberId: 1, 
    });


    // --- Effect Hooks for Initial Data Load ---

    useEffect(() => {
        fetchData();
        // Set default email to a unique value to avoid constant PK errors
        setMemberData(prev => ({...prev, email: `user_${Date.now()}@demo.com`}));
    }, []);

    const fetchData = async () => {
        try {
            const borrows = await apiRequest('GET', '/borrow');
            setBorrowList(borrows || []);
            
            const fines = await apiRequest('GET', '/fines');
            setFineList(fines || []);
        } catch (error) {
            // Only set message on major error, let component render if possible
        }
    };


    // --- HANDLERS for Input Changes ---

    const handleMemberChange = (e) => {
        const { name, value } = e.target;
        setMemberData({ ...memberData, [name]: value });
    };

    const handleBorrowChange = (e) => {
        const { name, value } = e.target;
        // Use the staff ID from the current active profile for the staffId field
        if (name === 'staffId') {
            setBorrowData({ ...borrowData, [name]: parseInt(value) });
            setCurrentProfile(prev => ({...prev, staffId: parseInt(value)}));
        } else {
            setBorrowData({ ...borrowData, [name]: value });
        }
    };
    
    const handleFineChange = (e) => {
        const { name, value } = e.target;
        setFineData({ ...fineData, [name]: value });
    };


    // --- API ACTION FUNCTIONS ---

    const handleCreateMember = async () => {
        try {
            const result = await apiRequest('POST', '/members', memberData);
            setMessage(`‚úÖ Member Created! ID: ${result.memberId}`);
        } catch (error) {
            setMessage(`‚ùå Failed to Create Member: ${error.message}`);
        }
    };

    const handleBorrowBook = async () => {
        try {
            const payload = {
                ...borrowData,
                memberId: currentProfile.memberId, // Use Member ID from current profile
                staffId: currentProfile.staffId // Use Staff ID from current profile
            };
            const result = await apiRequest('POST', '/borrow', payload);
            setMessage(`‚úÖ Book Borrowed! Borrow ID: ${result.borrowId}`);
            fetchData();
        } catch (error) {
            setMessage(`‚ùå Borrow Failed: ${error.message}`);
        }
    };

    const handleReturnBook = async () => {
        if (currentProfile.role !== 'Staff') {
            setMessage('‚ùå Access Denied: Only Staff can process returns.');
            return;
        }
        if (!returnId) {
            setMessage('Please enter a Borrow ID to return.');
            return;
        }
        const returnDate = new Date().toISOString().slice(0, 10);
        try {
            const url = `/borrow/return/${returnId}?returnDate=${returnDate}`;
            const result = await apiRequest('PUT', url);
            setMessage(`‚úÖ Borrow ID ${result.borrowId} Returned on ${returnDate}`);
            setReturnId(''); 
            fetchData();
        } catch (error) {
            setMessage(`‚ùå Return Failed (ID ${returnId}): ${error.message}`);
        }
    };
    
    const handleIssueFine = async () => {
        if (currentProfile.role !== 'Staff') {
            setMessage(`‚ùå Access Denied: Only Staff/Admin can issue fines.`);
            return;
        }
        try {
            const url = `/fines?borrowId=${fineData.borrowId}&staffId=${currentProfile.staffId}&amount=${fineData.amount}`;
            const result = await apiRequest('POST', url);
            setMessage(`‚úÖ Fine Issued! Fine ID: ${result.fineId}`);
            fetchData();
        } catch (error) {
            setMessage(`‚ùå Fine Issue Failed: ${error.message}`);
        }
    };

    const handlePayFine = async () => {
        if (!fineData.fineId) {
            setMessage('Please enter a Fine ID to pay.');
            return;
        }
        const datePaid = new Date().toISOString().slice(0, 10);
        try {
            const url = `/fines/pay/${fineData.fineId}?datePaid=${datePaid}`;
            const result = await apiRequest('PUT', url);
            setMessage(`‚úÖ Fine ID ${result.fineId} Paid on ${datePaid}`);
            fetchData();
        } catch (error) {
            setMessage(`‚ùå Fine Payment Failed (ID ${fineData.fineId}): ${error.message}`);
        }
    };


    // --- RENDER ---
    
    // Style variables defined at the bottom of the component for accessibility

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial', maxWidth: '1200px', margin: '0 auto' }}>
            <h1>üìö Library Backend Simulation</h1>
            
            {/* ROLE SIMULATOR */}
            <div style={{ 
                marginBottom: '30px', 
                padding: '15px', 
                backgroundColor: currentProfile.role === 'Staff' ? '#fff3cd' : '#e2f0d9', 
                border: '1px solid #ffc107'
            }}>
                <h2>üë• Demo Role Simulation</h2>
                <label htmlFor="role-select" style={{ marginRight: '15px', fontWeight: 'bold' }}>Act as:</label>
                <select 
                    id="role-select"
                    value={currentProfile.role}
                    onChange={(e) => {
                        const role = e.target.value;
                        const newStaffId = role === 'Staff' ? 1 : 0; 
                        const newMemberId = role === 'Member' ? 2 : 1; 
                        setCurrentProfile({
                            role: role,
                            staffId: newStaffId,
                            memberId: newMemberId 
                        });
                        // Update default IDs in forms
                        setBorrowData(prev => ({...prev, memberId: newMemberId, staffId: newStaffId || 1}));
                        setFineData(prev => ({...prev, staffId: newStaffId || 1}));
                        setMessage(`Switched to ${role} Mode. Staff ID: ${newStaffId}, Member ID: ${newMemberId}.`);
                    }}
                    style={{ padding: '8px', minWidth: '150px' }}
                >
                    <option value="Staff">Staff (Admin/Librarian)</option>
                    <option value="Member">Member (Borrower)</option>
                </select>
                <span style={{ marginLeft: '20px', fontSize: '14px' }}>
                    Current Role: <strong>{currentProfile.role}</strong> | 
                    Active Member ID for Borrow: <strong>{currentProfile.memberId}</strong>
                </span>
            </div>

            {/* Status Message */}
            <div style={{ 
                padding: '10px', 
                backgroundColor: message.startsWith('‚úÖ') ? '#d4edda' : message.startsWith('‚ùå') ? '#f8d7da' : '#eee',
                border: message ? '1px solid #ccc' : 'none',
                marginBottom: '20px',
                fontWeight: 'bold'
            }}>
                {message || 'Ready. Select a role and test the actions.'}
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: currentProfile.role === 'Staff' ? 'repeat(3, 1fr)' : 'repeat(2, 1fr)', gap: '20px' }}>
                
                {/* 1. Member Management Form (Shared Access) */}
                <div style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '5px' }}>
                    <h2 style={{ marginTop: 0 }}>üë§ Create New Member</h2>
                    <label style={labelStyle}>First Name:</label>
                    <input type="text" name="memberFirstName" value={memberData.memberFirstName} onChange={handleMemberChange} style={inputStyle} />
                    <label style={labelStyle}>Email:</label>
                    <input type="text" name="email" value={memberData.email} onChange={handleMemberChange} style={inputStyle} />
                    <label style={labelStyle}>Status:</label>
                    <select name="memberStatus" value={memberData.memberStatus} onChange={handleMemberChange} style={{...inputStyle, padding: '8px'}}>
                        <option value="Active">Active</option>
                        <option value="Not_Active">Not Active</option>
                    </select>
                    <button onClick={handleCreateMember} style={{ ...buttonStyle, backgroundColor: '#007bff' }}>
                        POST /members
                    </button>
                </div>
                
                {/* 2. Borrow/Return Cycle Form (Shared Access to Borrow) */}
                <div style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '5px' }}>
                    <h2 style={{ marginTop: 0 }}>üîÑ Borrow & Return</h2>
                    
                    <h3>Borrow (POST /borrow)</h3>
                    <label style={labelStyle}>Book Copy ID (e.g., 1):</label>
                    <input type="number" name="bookId" value={borrowData.bookId} onChange={handleBorrowChange} style={inputStyle} />
                    <label style={labelStyle}>Member ID (Current: {currentProfile.memberId}):</label>
                    <input type="number" name="memberId" value={currentProfile.memberId} disabled style={disabledInputStyle} />
                    <label style={labelStyle}>Due Date (YYYY-MM-DD):</label>
                    <input type="date" name="dueDate" value={borrowData.dueDate} onChange={handleBorrowChange} style={{...inputStyle, padding: '8px'}} />
                    <button onClick={handleBorrowBook} style={{ ...buttonStyle, backgroundColor: '#28a745' }}>
                        BORROW BOOK
                    </button>

                    <h3 style={{ marginTop: '20px' }}>Return (PUT /borrow/return/{returnId || 'ID'})</h3>
                    <label style={labelStyle}>Borrow ID to Return:</label>
                    <input type="number" value={returnId} onChange={(e) => setReturnId(e.target.value)} style={inputStyle} />
                    <button 
                        onClick={handleReturnBook} 
                        style={{ ...buttonStyle, backgroundColor: currentProfile.role === 'Staff' ? '#dc3545' : '#888' }} 
                        disabled={currentProfile.role !== 'Staff'}
                    >
                        RETURN BOOK ({currentProfile.role === 'Staff' ? 'Staff Only' : 'Member Access'})
                    </button>
                </div>

                {/* 3. Fine Management Form (STAFF ONLY - CONDITIONAL RENDERING) */}
                {currentProfile.role === 'Staff' && (
                    <div style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '5px', backgroundColor: '#f0f0f0' }}>
                        <h2 style={{ marginTop: 0 }}>üí∞ Fine Management (Staff Only)</h2>
                        
                        <h3>Issue Fine (POST /fines)</h3>
                        <label style={labelStyle}>Borrow ID:</label>
                        <input type="number" name="borrowId" value={fineData.borrowId} onChange={handleFineChange} style={inputStyle} />
                        <label style={labelStyle}>Amount:</label>
                        <input type="number" name="amount" value={fineData.amount} onChange={handleFineChange} style={inputStyle} />
                        <button onClick={handleIssueFine} style={{ ...buttonStyle, backgroundColor: '#ffc107', color: 'black' }}>
                            ISSUE FINE
                        </button>
                        
                        <h3 style={{ marginTop: '20px' }}>Pay Fine (PUT /fines/pay/{fineData.fineId || 'ID'})</h3>
                        <label style={labelStyle}>Fine ID to Pay:</label>
                        <input type="number" name="fineId" value={fineData.fineId} onChange={handleFineChange} style={inputStyle} />
                        <button onClick={handlePayFine} style={{ ...buttonStyle, backgroundColor: '#17a2b8' }}>
                            MARK PAID
                        </button>
                    </div>
                )}
            </div>

            {/* List of Current Records */}
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginTop: '30px' }}>
                <div>
                    <h2 style={{ marginTop: '0' }}>Current Borrow Records</h2>
                    <pre style={preStyle}>{JSON.stringify(borrowList, null, 2)}</pre>
                </div>
                <div>
                    <h2 style={{ marginTop: '0' }}>Current Fine Records</h2>
                    <pre style={preStyle}>{JSON.stringify(fineList, null, 2)}</pre>
                </div>
            </div>
        </div>
    );
}

// Simple styling objects for cleaner code
const inputStyle = { marginBottom: '10px', width: '90%', padding: '8px', boxSizing: 'border-box' };
const disabledInputStyle = { ...inputStyle, backgroundColor: '#e9ecef', color: '#6c757d' };
const buttonStyle = { padding: '10px', color: 'white', border: 'none', cursor: 'pointer', width: '90%', fontWeight: 'bold' };
const labelStyle = { display: 'block', marginBottom: '5px', fontWeight: '500', fontSize: '14px' };
const preStyle = { backgroundColor: '#f4f4f4', padding: '15px', borderRadius: '5px', maxHeight: '400px', overflowY: 'auto', whiteSpace: 'pre-wrap', wordBreak: 'break-all' };


export default LibraryDashboard;
